import React, {useState} from "react";
import {useNavigate} from "react-router-dom";



export default function WelcomeComponent(){
//function to know if a user is a host or a player, which button is clicked
    const [isPlayer, setIsPlayer] = useState(0)
    const [isHost, setIsHost] = useState(0)
    const navigate = useNavigate();

    function hostAction(){
        setIsHost(1);
        setIsPlayer(0);
        navigate("host/");
    }
    function playerAction(){
        setIsHost(0);
        setIsPlayer(1);
        navigate("player/");
    }

    return(
        <div className="WelcomeComponent">
            <h1>Welcome MAESTRO !</h1>
            <button type="button" onClick={hostAction}>Host</button>
            <button type="button" onClick={playerAction}>Player</button>
            {isHost && <div>COUCOUC</div>}
        </div>
    )
}
